CustomField.formula:
IF(
    OR(
        AND(
        /* Hub Handle and Hub Origin has default value set to 'OO' to prevent the user to be blocked when manually creating Cases */
            OR(NOT(ISBLANK(TEXT(HUB_Handle__c))),NOT(TEXT(HUB_Handle__c) = '00' )),
            TEXT(HUB_Handle__c) = TEXT($User.Hub__c)
        ),

        AND(
        /* Hub Handle and Hub Origin has default value set to 'OO' to prevent the user to be blocked when manually creating Cases */
            OR(NOT(ISBLANK(TEXT(HUB_Handle__c))),NOT(TEXT(HUB_Handle__c) = '00' )),
            OR(NOT(ISBLANK(TEXT(Hub_Origin__c))), NOT(TEXT(Hub_Origin__c) = '00' )),
            TEXT(Hub_Origin__c) = TEXT($User.Hub__c)
        )
    ),
true,
false)
